import bcrypt from "bcryptjs";
import { User } from "../models/User.js";
import { AppError } from "../utils/AppError.js";
import { asyncHandler } from "../utils/asyncHandler.js";
import { signAccessToken } from "../services/token.service.js";
import { env } from "../config/env.js";

export const register = asyncHandler(async (req, res) => {
  const {
    name,
    email,
    password,
    role,
    department,

    // student extra fields (optional if you didn’t implement profile model)
    registerNo,
    year,
    section,

    // admin register code
    adminRegisterCode
  } = req.body;

  const emailNorm = String(email || "").toLowerCase().trim();

  const exists = await User.findOne({ email: emailNorm });
  if (exists) throw new AppError("Email already exists", 409);

  // protect admin creation
  if (role === "admin") {
    if (!env.ADMIN_REGISTER_CODE) throw new AppError("Admin registration disabled", 403);
    if (adminRegisterCode !== env.ADMIN_REGISTER_CODE) throw new AppError("Invalid admin register code", 403);
  }

  const hash = await bcrypt.hash(password, 10);

  const user = await User.create({
    name,
    email: emailNorm,
    password: hash, // ✅ store hash in "password"
    role,
    department
  });

  const accessToken = signAccessToken(user._id.toString());

  res.status(201).json({
    success: true,
    accessToken,
    user: {
      id: user._id,
      name: user.name,
      email: user.email,
      role: user.role,
      department: user.department
    }
  });
});

export const login = asyncHandler(async (req, res) => {
  const email = String(req.body.email || "").toLowerCase().trim();
  const password = String(req.body.password || "");

  const user = await User.findOne({ email });
  if (!user) throw new AppError("Invalid credentials", 401);
  if (user.isActive === false) throw new AppError("Account disabled", 403);

  // ✅ Your DB uses "password" field for hash
  const ok = await bcrypt.compare(password, user.password);
  if (!ok) throw new AppError("Invalid credentials", 401);

  const accessToken = signAccessToken(user._id.toString());

  res.json({
    success: true,
    accessToken,
    user: {
      id: user._id,
      name: user.name,
      email: user.email,
      role: user.role,
      department: user.department
    }
  });
});
